import React, { Component } from 'react'
import { CSSTransition, Transition, TransitionGroup } from 'react-transition-group';
import { TimelineLite, CSSPlugin } from 'gsap';
 
class About extends Component {

	constructor(props){
		super(props);

		this.aboutContainer = null;

		this.aboutTween = null;
	}

	componentDidMount() {
		this.aboutTween = new TimelineLite({ paused:true })
			.to(this.aboutContainerLeft && this.aboutContainerRight, 1.4, { opacity: 0, x: -750 });
	}

	render() {
		  return (
		    <div className="body-wrapper">
		    	<div onClick={ () => this.aboutTween.play() }>
		    		<div ref={ div  => this.aboutContainerLeft = div }>Block Left</div>
		    		<div ref={ div  => this.aboutContainerRight = div }>Block Right</div>
			    </div>
		    </div>
		  )
		}
}

export default About;
